@Client.on_message(filters.user(Sudos) & filters.command(["delayspam"], prefixes=handler))
@Client.on_message(filters.me & filters.command(["delayspam"], prefixes=handler))
async def delayspam(KanishkaXJungli: Client, e: Message): 
    usage = spam_usage.delayspam
    Krishna = "".join(e.text.split(maxsplit=1)[1:]).split(" ", 2)
    Krishnaop = Krishna[1:]
    if len(Krishnaop) == 2:
       counts = int(Krishnaop[0])
       spam_text = str(Krishnaop[1])
       sleeptime = float(Krishna[0])
       await start_dspam(KanishkaXJungli, e, counts, sleeptime, spam_text)
    else:
        await e.reply_text(usage.format(handler))
        return

    if LOGS_CHANNEL:
         try:
             await KanishkaXJungli.send_message(LOGS_CHANNEL, f"started Delay Spam By User: {e.from_user.id} \n\n Chat: {e.chat.id} \n Counts: {counts} \n Spam Message: {msg} \n Delay Time: {sleeptime}")
         except Exception as a:
             print(a)
             pass


